<?phpnamespace Extanet\BeautyAwards\Database;class Tables {    static function exists($table_name) {        global $wpdb;        return in_array($table_name, $wpdb->get_col('SHOW TABLES;'));    }    static function create($table_name, $fields) {        if (self::exists($table_name)) {            return;        }                if (empty($fields)){            return;        }        global $wpdb;        $query = "";        $query .= "CREATE TABLE `" . $table_name . "` (" . "\n";        $query .= "`id` INT NOT NULL AUTO_INCREMENT," . "\n";        foreach ($fields as $field) {            $query .= "`" . $field->name . "` " . $field->type . " NULL," . "\n";        }        $query .= "";        $query .= "PRIMARY KEY (`id`)" . "\n";        $query .= ") " . $wpdb->get_charset_collate() . ";";        $wpdb->query($query);    }}