<?phpuse Extanet\BeautyAwards\Core\Categories as Categories;use Extanet\BeautyAwards\Core\Contest;use Extanet\BeautyAwards\Core\EntryImage;use Extanet\BeautyAwards\FrontEnd\EntryForm;use Extanet\BeautyAwards\FrontEnd\Entries;?><form id="form_entries_list" action="<?php echo filter_input(INPUT_POST, ''); ?>" method="POST" enctype="multipart/form-data">    <?php Entries::view_nonce(); ?>    <h2>Got it! You can continue to submit or add more entries</h2>    <?php EntryForm::view_post_message(); ?>    <?php $category_total = 0; ?><?php foreach (Entries::get_entries() as $index => $entry): ?>        <div class="entries-item">            <div class="entries-item-picture" style="background-image: url('<?php echo EntryImage::get_source($entry->images[0]); ?>');">sdf</div>            <h3 class="entries-item-title"><?php echo $entry->title; ?></h3>            <p><?php echo $entry->description; ?></p>            <table class="entries-item-categories">                <tbody>    <?php foreach ($entry->categories as $category_id): ?>                        <tr>                            <td class="col-category-name"><?php echo Categories::get_name($category_id); ?></td>                            <td class="col-category-price">$<?php echo Contest::entry_fee(); ?></td>                        </tr>                        <?php $category_total += Contest::entry_fee(); ?>                <?php endforeach; ?>                </tbody>    <?php if (Entries::count_entries() == 1): ?>                    <tfoot>                        <tr>                            <td class="col-category-name">Total</td>                            <td class="col-category-price">$<?php echo $category_total; ?></td>                        </tr>                    </tfoot>    <?php endif; ?>            </table>            <p class="entries-item-controls">                <a href="<?php echo Entries::link_edit_entry($index); ?>">Edit this entry</a>                <a href="<?php echo Entries::link_delete_entry($index); ?>">Delete this entry</a>            </p>        </div>    <?php endforeach; ?><?php if (Entries::count_entries() > 1): ?>        <div class="entries-item">            <table class="entries-item-total">                <tfoot>                    <tr>                        <td class="col-category-name">Total</td>                        <td class="col-category-price">$<?php echo $category_total; ?></td>                    </tr>                </tfoot>            </table>        </div><?php endif; ?>    <p>        <button id="btn_add_entry" name="add_entry" value="1" class="button-secondary">            Add another entry?        </button>    </p><?php if (Entries::count_entries() > 0): ?>        <p class="submit">            <button id="btn_confirm_entries" name="confirm_entries" value="1" class="button-primary">                Continue            </button>        </p><?php endif; ?></form>