<?phpuse Extanet\BeautyAwards\Core\EntryImage;use Extanet\BeautyAwards\FrontEnd\Entries;use Extanet\BeautyAwards\FrontEnd\EntryForm;use Extanet\BeautyAwards\FrontEnd\EntryDetails;?><script type="text/javascript">    var globaw = {        image_upload_url: '<?php echo EntryDetails::link_image_upload(); ?>',        allowed_mime_types: <?php echo json_encode(EntryImage::allowed_mime_types); ?>,        max_file_size: <?php echo EntryImage::max_file_size; ?>,        max_file_size_string: '<?php echo EntryImage::max_file_size_string(); ?>',        min_image_width: <?php echo EntryImage::min_image_width; ?>,        min_image_height: <?php echo EntryImage::min_image_height; ?>    };</script><form id="form_entry_details" action="<?php echo filter_input(INPUT_POST, ''); ?>" method="POST" enctype="multipart/form-data">    <?php EntryDetails::view_nonce(); ?>    <?php if (count(Entries::get_entries()) == 1): ?>        <h2>Alright! Let's submit your first entry.</h2>    <?php else: ?>        <h2>Let's submit another entry.</h2>    <?php endif; ?>    <?php EntryForm::view_post_message(); ?>    <!-- image -->    <?php $image_id = (!is_null(EntryForm::$p) ? EntryForm::$p->get_data('image_id') : Entries::image_id(Entries::editing_index())); ?>    <p id="par_upload_message" style="display: none;"></p>    <div id="box_file_upload" class="dropload<?php echo $image_id > 0 ? ' dropload-has-file' : ''; ?>">        <input type="hidden" id="hid_image_id" name="image_id" value="<?php echo $image_id; ?>" />        <input type="file" id="fup_image" name="image" class="dropload-input" />        <div class="dropload-preview"<?php echo $image_id > 0 ? ' style="background-image: url(\'' . EntryImage::get_source($image_id) . '\');"' : ''; ?>></div>        <label for="fup_image">            <strong>Choose a file</strong>            <span>or drag it here</span>        </label>        <div class="dropload-drop-area"></div>        <div class="dropload-uploading"><span></span></div>    </div>    <p><a href class="modal-open">Photo tips</a></p>    <!-- name -->    <p>        <input type="text" id="txt_title" name="title" placeholder="Entry Title" value="<?php echo (!is_null(EntryForm::$p)) ? EntryForm::$p->get_data('title') : Entries::title(Entries::editing_index()); ?>" />    </p>    <p>"Beach Bride", or "High-Fade"</p>    <!-- description -->    <p>What do you want the judges to know about this entry, did you use any special techniques?</p>    <p>        <textarea id="txt_description" name="description" placeholder="Short Entry Description" rows="7"><?php echo (!is_null(EntryForm::$p)) ? EntryForm::$p->get_data('description') : Entries::description(Entries::editing_index()); ?></textarea>    </p>    <p class="submit">        <button id="btn_submit_entry_details" name="submit_entry_details" value="1" class="button-primary">            Continue to categories        </button>    </p>    <p>        <a href="<?php echo EntryDetails::link_back_to_industries(); ?>">Back to industries</a>        <?php if (Entries::count_entries() > 1): ?>            <a href="<?php echo EntryDetails::link_cancel_new_entry(); ?>">Cancel this new entry</a>        <?php endif; ?>    </p></form>