<?phpuse Extanet\BeautyAwards\WPAdmin\Judge\ToJudge;use Extanet\BeautyAwards\Core\Categories;use Extanet\BeautyAwards\Core\Contest;use Extanet\BeautyAwards\Core\Entries;use Extanet\BeautyAwards\Core\EntryImage;use Extanet\BeautyAwards\Core\Judges;$category_entry_id = ToJudge::get_category_entry_id_from_get();$category_entry = Entries::get_category_entry($category_entry_id);$entry_id = $category_entry->entry_id;$entry = Entries::get_entry($entry_id);$link_prev = ToJudge::link_previous($category_entry_id);$link_next = ToJudge::link_next($category_entry_id);$score = Judges::get_score($category_entry_id);?><div id="box_to_judge" class="wrap judge">    <h1 class="wp-heading-inline">Judge Entry</h1>    <hr class="wp-header-end" />    <?php if (!Contest::status() || time() > Contest::end_date()): ?>        <p>Thank you for Judging! Judging is currently closed. <?php echo date('M j, Y', Contest::start_date()); ?> to <?php echo date('M j, Y', Contest::end_date()); ?></p>    <?php else: ?>        <p>            <a href="<?php echo ToJudge::link_list(); ?>" class="button-secondary">                <span class="dashicons dashicons-arrow-left-alt2"></span>                Back to list            </a>            <?php if (!is_null($link_prev) || !is_null($link_next)): ?>                <span class="dashicons dashicons-minus"></span>                <?php if (!is_null($link_prev)): ?>                    <a href="<?php echo ToJudge::link_previous($category_entry_id); ?>" class="button-secondary">                        <span class="dashicons dashicons-arrow-left-alt"></span>                        Previous Entry                    </a>                <?php endif; ?>                <?php if (!is_null($link_next)): ?>                    <a href="<?php echo ToJudge::link_next($category_entry_id); ?>" class="button-secondary">                        Next Entry                        <span class="dashicons dashicons-arrow-right-alt"></span>                    </a>                <?php endif; ?>            <?php endif; ?>        </p>        <table class="judge-preview">            <tbody>                <tr>                    <td class="image-holder">                        <div style="background-image: url('<?php echo EntryImage::get_source(Entries::get_images($entry->id)[0]); ?>');"></div>                    </td>                    <td>                        <form action="<?php echo $_SERVER['REQUEST_URI']; ?>" method="POST">                            <?php wp_nonce_field('judge_entry', 'session_marker'); ?>                            <input type="hidden" name="category_entry_id" value="<?php echo $category_entry_id; ?>" />                            <table class="form-table">                                <tbody>                                    <tr>                                        <th scope="row">Title</th>                                        <td><?php echo $entry->title; ?></td>                                    </tr>                                    <tr>                                        <th scope="row">Description</th>                                        <td><?php echo $entry->description; ?></td>                                    </tr>                                    <tr>                                        <th scope="row">Category</th>                                        <td>                                            <?php echo Categories::get_name(Categories::get_parent_id($category_entry->category_id)); ?><br />                                            <?php echo Categories::get_name($category_entry->category_id); ?>                                        </td>                                    </tr>                                    <tr>                                        <th scope="row">Your rating</th>                                        <td>                                            <select name="rating" class="widefat">                                                <option value="0">(select rating)</option>                                                <?php for ($counter = 1; $counter <= 10; $counter++): ?>                                                    <option value="<?php echo $counter; ?>"<?php echo (!is_null(ToJudge::$p) ? ToJudge::$p->get_data('rating') : $score->score) == $counter ? ' selected="selected"' : ''; ?>><?php echo $counter; ?></option>                                                <?php endfor; ?>                                            </select>                                        </td>                                    </tr>                                    <tr>                                        <th scope="row">Your comment</th>                                        <td>                                            <textarea name="comment" rows="4" class="widefat"><?php echo (!is_null(ToJudge::$p)) ? ToJudge::$p->get_data('comment') : $score->comment; ?></textarea>                                        </td>                                    </tr>                                </tbody>                            </table>                            <p class="submit">                                <button id="btn_judge_entry" name="judge_entry" class="button-primary">                                    Judge Entry                                </button>                            </p>                        </form>                    </td>                </tr>            </tbody>        </table>    <?php endif; ?></div>