<?phpuse Extanet\BeautyAwards\Core\Contest;use Extanet\BeautyAwards\Core\Judges;use Extanet\BeautyAwards\WPAdmin\Judge\ToJudge;$judged = Judges::get_judged(get_current_user_id());?><div id="box_judged" class="wrap">    <h1 class="wp-heading-inline">Judged</h1>    <hr class="wp-header-end" />    <?php if ((!Contest::status() || time() < Contest::end_date()) && FALSE): ?>        <p>Thank you for Judging! Judging is currently closed. <?php echo date('M j, Y', Contest::start_date()); ?> to <?php echo date('M j, Y', Contest::end_date()); ?></p>    <?php else: ?>        <?php if (empty($judged)): ?>            <p>You haven't judged any entry.</p>        <?php else: ?>            <table class="widefat striped">                <thead>                    <tr>                        <th>Title</th>                        <th>Description</th>                        <th>Category</th>                        <th class="col-rating">Rating</th>                        <th>Comment</th>                        <th class="col-judge"></th>                    </tr>                </thead>                <tbody>                    <?php foreach ($judged as $tji): ?>                        <tr>                            <td><?php echo $tji->title; ?></td>                            <td><?php echo $tji->description; ?></td>                            <td><?php echo $tji->name; ?></td>                            <td class="col-rating">                                <strong><?php echo $tji->score; ?></strong>/10                            </td>                            <td><?php echo $tji->comment; ?></td>                            <td class="col-judge">                                <a href="<?php echo ToJudge::link_judge($tji->id, TRUE); ?>" class="button-secondary">Judge</a>                            </td>                        </tr>                    <?php endforeach; ?>                </tbody>            </table>        <?php endif; ?>    <?php endif; ?></div>